Handlebars.registerHelper('transformToc', function (toc, options) {
  var result = options.fn(this);
  var tocHtml = '<div class="toc">' + Handlebars.helpers.tocToHtml.call(this, toc) + '</div>';

  // Remplacer les liens hypertexte par du texte simple
  tocHtml = tocHtml.replace(/<a[^>]*>/g, '').replace(/<\/a>/g, '');

  return new Handlebars.SafeString(
    result.replace(/\[toc\]/gi, tocHtml)
  );
});
